const API_URL = 'https://raw.githubusercontent.com/github/gemoji/master/db/emoji.json'
const requestEmojis = async () => await fetch(API_URL)

export default {
    state: {
        originalEmojis: [],
        showEmojis: [],
    },
    mutations: {
        setEmojis(state, emojis) {
            state.showEmojis = emojis
            state.originalEmojis = emojis
        },
        setOriginalEmoji(state, search) {
            const filtered = search.length > 1
                ? state.originalEmojis.filter((emoji) => emoji.aliases[0].includes(search))
                : [...state.originalEmojis]
            state.showEmojis = filtered
        }
    },
    actions: {
        async fetchEmojis({ commit }) {
            const res = await requestEmojis()
	        const emojis = await res.json()
            commit('setEmojis', emojis)
        },
        filterEmojis({ commit }, search) {
            commit('setOriginalEmoji', search)
        }
    },
    getters: {
        originalEmojis: (state) => state.originalEmojis,
        showEmojis: (state) => state.showEmojis,
    },
}
